import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

const App = () => {
  const [studentVerified, setStudentVerified] = useState('');
  const [studentName, setStudentName] = useState('Maya'); 

  useEffect(() => {
    const socket = io('http://localhost:3000');

    socket.on("connect", () => {
      console.log("You connected with id", socket.id);
      socket.emit('studentInfo', studentName);
    });

    socket.on("studentVerification", (verificationStatus) => {
      console.log(verificationStatus);
      setStudentVerified(verificationStatus);
    });

    // Cleanup the effect
    return () => socket.disconnect();
  }, [studentName]); // Add studentName to the dependency array to reconnect with new name

  const handleNameChange = (e) => {
    setStudentName(e.target.value);
  };

  return (
    <div>
      <h1>Language Master</h1>
      <label>
        Enter Name:
        <input type="text" value={studentName} onChange={handleNameChange} />
      </label>
      <h2>{studentVerified ? 'Student Verified' : 'Student Not Verified'}</h2>
    </div>
  );
};

export default App;
